// Demo.js
import { useState } from "react";
import { login as apiLogin, getMe, logout as apiLogout } from "./auth";
import { useAuth } from "./AuthContext";
import './log.css';

export default function Demo() {
  const { user, login, logout } = useAuth(); // use global auth context
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      await apiLogin(email, password);       // hit backend, store tokens
      const me = await getMe();              // fetch user info
      login(me);                             // save user in context
      setError("");
    } catch (err) {
      setError("Invalid email or password");
    }
  };

  const handleLogout = async () => {
    await apiLogout();  // clear backend/session
    logout();           // clear context
  };

  return (
    <div className="log-page">
      <div></div>
      <div className="logDiv">
        {!user ? (
          <form className="login-form" onSubmit={handleLogin}>
            <div className="form-group">
              <div className="input-wrapper">
                <input
                  type="email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  required
                />
                <label>Email:</label>
                <span className="focus-border"></span>
              </div>
            </div>

            <div className="form-group">
              <div className="input-wrapper password-wrapper">
                <label>Password:</label>
                <input
                  type="password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  required
                />
              </div>
            </div>

            <div className="form-options">
              <label className="remember-wrapper">
                <input type="checkbox" id="remember" name="remember" />
                <span className="checkbox-label">
                  <span className="checkmark"></span>
                  Remember me
                </span>
              </label>
              <a href="#" className="forgot-password">Forgot password?</a>
            </div>

            <button type="submit">
              <span className="btn-text">Sign In</span>
              <span className="btn-loader"></span>
            </button>
            {error && <p style={{ color: "red" }}>{error}</p>}
          </form>
        ) : (
          <div>
            <p>Welcome, {user.username || user.email}!</p>
            <button onClick={handleLogout}>Logout</button>
            <pre>{JSON.stringify(user, null, 2)}</pre>
          </div>
        )}
      </div>
      <div></div>
    </div>
  );
}
