// Demo.js
import { useState } from "react";
import { login as apiLogin, getMe, logout as apiLogout } from "./auth";
import { useAuth } from "./AuthContext";  // import context
import './log.css';

export default function Demo() {
  const { user, login, logout } = useAuth(); // grab from context
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      await apiLogin(email, password); // store tokens
      const me = await getMe();
      login(me); // update global auth state
      setError("");
    } catch (err) {
      setError("Invalid email or password");
    }
  };

  const handleLogout = async () => {
    await apiLogout();
    logout(); // clear context
  };

  return (
    <div className="log-page">
      <div className="logDiv">
        {!user ? (
          <form className="login-form" onSubmit={handleLogin}>
            <div className="form-group">
              <div className="input-wrapper">
                <input
                  type="email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  required
                />
                <label>Email:</label>
                <span className="focus-border"></span>
              </div>
            </div>

            <div className="form-group">
              <div className="input-wrapper password-wrapper">
                <label>Password:</label>
                <input
                  type="password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  required
                />
              </div>
            </div>

            <button type="submit">
              <span className="btn-text">Sign In</span>
              <span className="btn-loader"></span>
            </button>
            {error && <p style={{ color: "red" }}>{error}</p>}
          </form>
        ) : (
          <div>
            <p>Welcome, {user.username || user.email}!</p>
            <button onClick={handleLogout}>Logout</button>
            <pre>{JSON.stringify(user, null, 2)}</pre>
          </div>
        )}
      </div>
    </div>
  );
}
