import React, { useEffect, useState } from "react";
import "./BookList.css";

const BASE_URL = "http://127.0.0.1:8000"; // change this to your API baseurl
const FALLBACK_COVER =
  "https://upload.wikimedia.org/wikipedia/commons/thumb/a/ac/No_image_available.svg/480px-No_image_available.svg.png";

const BookList = () => {
  const [books, setBooks] = useState([]);

  useEffect(() => {
    fetch(`${BASE_URL}/books/`)
      .then((res) => res.json())
      .then((data) => setBooks(data))
      .catch((err) => console.error("Error fetching books:", err));
  }, []);

  return (
    <div className="bookstore-container">
      <h1 className="store-title">Online Bookstore</h1>
      <div className="book-grid">
        {books.map((book) => (
          <a key={book.id} href={`/${book.slug}`} className="book-card">
            <img
              src={book.pdf_file ? book.pdf_file : FALLBACK_COVER}
              alt={book.title}
              className="book-cover"
              onError={(e) => (e.target.src = FALLBACK_COVER)}
            />
            <div className="book-info">
              <h2 className="book-title">{book.title}</h2>
              <p className="book-author">by {book.author}</p>
              <p className="book-meta">
                Purchases: {book.purchase_count ?? 0}
              </p>
            </div>
          </a>
        ))}
      </div>
    </div>
  );
};

export default BookList;
