import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import './CourseSelection.css';

const CourseSelection = () => {
  const [courses, setCourses] = useState([]);
  const [selectedCourse, setSelectedCourse] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const { isAuthenticated } = useAuth();
  const navigate = useNavigate();

  useEffect(() => {
    fetchCourses();
  }, []);

  const fetchCourses = async () => {
    try {
      setLoading(true);
      const response = await fetch('/api/courses/', {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('access_token')}`,
          'Content-Type': 'application/json',
        },
      });

      if (!response.ok) {
        throw new Error('Failed to fetch courses');
      }

      const data = await response.json();
      setCourses(data);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleCourseSelect = (course) => {
    setSelectedCourse(course);
  };

  const handleNext = async () => {
    if (!selectedCourse) {
      alert('Please select a course first');
      return;
    }

    try {
      const response = await fetch('/api/my-courses/', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('access_token')}`,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          course: selectedCourse.id
        }),
      });

      if (!response.ok) {
        throw new Error('Failed to save course selection');
      }

      // Navigate to subject selection with course data
      navigate('/subject-selection', { 
        state: { 
          selectedCourse: selectedCourse 
        } 
      });
    } catch (err) {
      setError(err.message);
    }
  };

  if (loading) {
    return (
      <div className="course-selection-container">
        <div className="loading-spinner">
          <div className="spinner"></div>
          <p>Loading courses...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="course-selection-container">
        <div className="error-message">
          <p>Error: {error}</p>
          <button onClick={fetchCourses}>Try Again</button>
        </div>
      </div>
    );
  }

  return (
    <div className="course-selection-container">
      <div className="course-selection-content">
        <div className="course-selection-header">
          <h1>Choose Your Course</h1>
          <p>Select the course you want to study</p>
        </div>

        <div className="courses-grid">
          {courses.map((course) => (
            <div
              key={course.id}
              className={`course-card ${selectedCourse?.id === course.id ? 'selected' : ''}`}
              onClick={() => handleCourseSelect(course)}
            >
              <div className="course-icon">
                {course.code === 'kcse' && '🎓'}
                {course.code === 'cpa' && '📊'}
                {course.code === 'kmtc' && '🏥'}
                {course.code === 'acca' && '💼'}
              </div>
              <h3>{course.name}</h3>
              <p>Click to select this course</p>
            </div>
          ))}
        </div>

        <div className="course-selection-actions">
          <button
            className="next-button"
            onClick={handleNext}
            disabled={!selectedCourse}
          >
            Next
          </button>
        </div>
      </div>
    </div>
  );
};

export default CourseSelection;
